import React, {useLayoutEffect} from "react";
import {Link} from 'react-router-dom';

import './styles.scss';

export default function Navegation(props) {
  useLayoutEffect(() => {
    const a = document.querySelector(".choosenOne");
    const b = document.querySelector(".div--animation ");
    b.style.left = `${a.offsetLeft}px`;
    b.style.width = `${a.offsetWidth}px`;
  });

  return (
    <>
      <div className="App"></div>

      <div className="div--animation"></div>

      <div className="div--wrapper">
        <Link className={props.name === "/" ? "choosenOne" : null} to="/">
          Home
        </Link>
        <Link className={props.name === "/gallery" ? "choosenOne" : null} to="/">
          Gallery
        </Link>
        <Link className={props.name === "/about" ? "choosenOne" : null} to="/">
          About
        </Link>
        <Link className={props.name === "/contact" ? "choosenOne" : null} to="/">
          Contact
        </Link>
      </div>
    </>
  );
}
